    border-radius: 4px;
}

.menuButton {
    border-radius: 4px;
    display: flex;
    width: 100%;
    gap: 8px;
    padding: 8px;
    cursor: pointer;
}

.menuButton:hover {
    background: var(--light);
} 
.red {
